mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, A.* FROM ( SELECT * FROM MEMBER M JOIN DEPARTMENT D ON M.DEPT_NO=D.DEPT_NO JOIN POSITION P ON M.POS_NO=P.POS_NO AND MEM_RESIGNDATE IS NULL
ORDER BY MEM_NO DESC )A )
WHERE RNUM>#{firstRecordIndex} AND RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
UPDATE MEMBER
SET MEM_NAME=#{memName}, MEM_HP1=#{memHp1}, MEM_HP2=#{memHp2}, MEM_HP3=#{memHp3},
	MEM_PAY=#{memPay}, MEM_HOLIDAY=#{memHoliday}, DEPT_NO=#{deptNo},
	POS_NO=#{posNo}, RANKS_NO=#{ranksNo}
WHERE MEM_NO=#{memNo}

UPDATE MEMBER
SET MEM_RESIGNDATE=SYSDATE
WHERE MEM_NO=#{memNo}

UPDATE MEMBER
SET MEM_RESIGNDATE=NULL
WHERE MEM_NO=#{memNo}
