mapper.select:
SELECT * FROM JAWONMANAGER_VIEW
WHERE TYPE_NO=#{typeNo}
ORDER BY TYPE_NO

SELECT * FROM JAWONLIST_VIEW
WHERE MEM_NO=#{memNo}
ORDER BY RESERV_NO DESC

mapper.update:
UPDATE RESOURCES
SET RES_NAME=#{resName}, RES_LOCATION=#{resLocation}, RES_LAT=#{resLat}, RES_LNG=#{resLng}
WHERE RES_NO=#{resNo}
